import { TestBed, ComponentFixture } from '@angular/core/testing';
import { ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { HttpClientTestingModule } from '@angular/common/http/testing'; // Questo modulo è necessario per i test con HttpClient
import { of } from 'rxjs';
import { AttivitaComponent } from './attivita.component';
import { AttivitaService } from '../../services/attivita.service';
import { AuthService } from '../../services/authService';

describe('AttivitaComponent', () => {
  let component: AttivitaComponent;
  let fixture: ComponentFixture<AttivitaComponent>;
  let attivitaServiceSpy: jasmine.SpyObj<AttivitaService>;
  let authServiceSpy: jasmine.SpyObj<AuthService>;
  let routerSpy: jasmine.SpyObj<Router>;

  beforeEach(async () => {
    // Crea spy per i servizi
    const attivitaSpy = jasmine.createSpyObj('AttivitaService', ['getAttivitaByUser', 'createAttivita', 'updateAttivita', 'deleteAttivita']);
    const authSpy = jasmine.createSpyObj('AuthService', ['getUserId', 'logout']);
    const routerMock = jasmine.createSpyObj('Router', ['navigate']);

    // Configura il TestBed con il modulo di test HTTP
    await TestBed.configureTestingModule({
      imports: [
        AttivitaComponent, // Importa il componente standalone
        ReactiveFormsModule, 
        HttpClientTestingModule // Usa questo modulo per mockare le richieste HTTP
      ],
      providers: [
        { provide: AttivitaService, useValue: attivitaSpy },
        { provide: AuthService, useValue: authSpy },
        { provide: Router, useValue: routerMock }
      ]
    }).compileComponents();

    // Crea il componente
    fixture = TestBed.createComponent(AttivitaComponent);
    component = fixture.componentInstance;

    // Inietta gli spy
    attivitaServiceSpy = TestBed.inject(AttivitaService) as jasmine.SpyObj<AttivitaService>;
    authServiceSpy = TestBed.inject(AuthService) as jasmine.SpyObj<AuthService>;
    routerSpy = TestBed.inject(Router) as jasmine.SpyObj<Router>;

    fixture.detectChanges(); // Rilevamento dei cambiamenti
  });

  it('should create', () => {
    expect(component).toBeTruthy();
  });

  describe('ngOnInit', () => {
    it('should load activities on init', () => {
      const mockAttivita = [
          { id: 1, titolo: 'Attività 1', descrizione: 'Descrizione 1', scadenza: '2024-01-01', stato: 'Da fare', idUser: '1' },
        ];

      attivitaServiceSpy.getAttivitaByUser.and.returnValue(of(mockAttivita));

      component.ngOnInit();

      expect(attivitaServiceSpy.getAttivitaByUser).toHaveBeenCalled();
      expect(component.attivitaList.length).toBe(1);
      expect(component.attivitaList[0].titolo).toBe('Attività 1');
    });

    it('should handle error when loading activities', () => {
      attivitaServiceSpy.getAttivitaByUser.and.returnValue(throwError({ status: 401 }));

      component.ngOnInit();

      expect(attivitaServiceSpy.getAttivitaByUser).toHaveBeenCalled();
      expect(routerSpy.navigate).toHaveBeenCalledWith(['/auth']);
    });
  });

  describe('addAttivita', () => {
    it('should add a new activity successfully', () => {
      const mockAttivita = { id: 2, titolo: 'New Attività', descrizione: 'Nuova descrizione', scadenza: '2024-02-01', stato: 'Da fare', idUser: '1' };
      attivitaServiceSpy.createAttivita.and.returnValue(of(mockAttivita));
      authServiceSpy.getUserId.and.returnValue('1');

      component.attivitaForm.setValue({
        id: 0,
        titolo: 'New Attività',
        descrizione: 'Nuova descrizione',
        scadenza: '2024-02-01',
        stato: 'Da fare',
        idUser: '1',
      });

      component.addAttivita();

      expect(attivitaServiceSpy.createAttivita).toHaveBeenCalledWith(jasmine.objectContaining({
        titolo: 'New Attività',
        descrizione: 'Nuova descrizione',
        scadenza: jasmine.any(String),
        stato: 'Da fare',
        idUser: '1',
      }));
      expect(component.attivitaList.length).toBe(1);
      expect(component.attivitaList[0].titolo).toBe('New Attività');
    });

    it('should not add activity if form is invalid', () => {
      component.attivitaForm.setValue({
        id: 0,
        titolo: '',
        descrizione: '',
        scadenza: '',
        stato: 'Da fare',
        idUser: '1',
      });

      component.addAttivita();

      expect(attivitaServiceSpy.createAttivita).not.toHaveBeenCalled();
    });
  });

  describe('updateAttivita', () => {
    it('should update an existing activity successfully', () => {
      const mockUpdatedAttivita = {
        id: 1,
        titolo: 'Updated Attività',
        descrizione: 'Updated descrizione',
        scadenza: '2024-02-01T00:00:00Z',
        stato: 'In corso',
        idUser: '1',
      };
      attivitaServiceSpy.updateAttivita.and.returnValue(of(void(0)));

      component.selectedAttivita = { id: 1, titolo: 'Attività 1', descrizione: 'Descrizione 1', scadenza: '2024-01-01', stato: 'Da fare', idUser: '1' };
      component.attivitaForm.setValue(mockUpdatedAttivita);

      component.updateAttivita();

      expect(attivitaServiceSpy.updateAttivita).toHaveBeenCalledWith(mockUpdatedAttivita.id, mockUpdatedAttivita);
      expect(component.attivitaList[0]).toEqual(mockUpdatedAttivita);
    });

    it('should not update if form is invalid or no activity selected', () => {
      component.selectedAttivita = null;
      component.attivitaForm.setValue({
        id: 1,
        titolo: '',
        descrizione: '',
        scadenza: '',
        stato: '',
        idUser: '1',
      });

      component.updateAttivita();

      expect(attivitaServiceSpy.updateAttivita).not.toHaveBeenCalled();
    });
  });

  describe('deleteAttivita', () => {
    it('should delete an activity successfully', () => {
      attivitaServiceSpy.deleteAttivita.and.returnValue(of(void(0)));
      component.attivitaList = [{ id: 1, titolo: 'Attività 1', descrizione: 'Descrizione 1', scadenza: '2024-01-01', stato: 'Da fare', idUser: '1' }];

      component.deleteAttivita(1);

      expect(attivitaServiceSpy.deleteAttivita).toHaveBeenCalledWith(1);
      expect(component.attivitaList.length).toBe(0);
    });
  });

  describe('logout', () => {
    it('should call authService.logout and navigate to auth page', () => {
      component.logout();

      expect(authServiceSpy.logout).toHaveBeenCalled();
      expect(routerSpy.navigate).toHaveBeenCalledWith(['/auth']);
    });
  });
});
