import { TestBed, ComponentFixture } from '@angular/core/testing';
import { ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { of } from 'rxjs';
import { AttivitaComponent } from './attivita.component';
import { AttivitaService } from '../../services/attivita.service';
import { AuthService } from '../../services/authService';
import { HttpClientTestingModule } from '@angular/common/http/testing';

describe('AttivitaComponent', () => {
  let component: AttivitaComponent;
  let fixture: ComponentFixture<AttivitaComponent>;
  let attivitaServiceSpy: jasmine.SpyObj<AttivitaService>;
  let authServiceSpy: jasmine.SpyObj<AuthService>;
  let routerSpy: jasmine.SpyObj<Router>;

  beforeEach(() => {
    const attivitaSpy = jasmine.createSpyObj('AttivitaService', ['getAttivitaByUser', 'createAttivita', 'updateAttivita', 'deleteAttivita']);
    const authSpy = jasmine.createSpyObj('AuthService', ['getUserId', 'logout']);
    const routerMock = jasmine.createSpyObj('Router', ['navigate']);

    TestBed.configureTestingModule({
      imports: [
        ReactiveFormsModule,
        HttpClientTestingModule, // Importa il modulo per HttpClient
        AttivitaComponent // Importa il componente standalone direttamente
      ],
      providers: [
        { provide: AttivitaService, useValue: attivitaSpy },
        { provide: AuthService, useValue: authSpy },
        { provide: Router, useValue: routerMock }
      ]
    }).compileComponents();

    fixture = TestBed.createComponent(AttivitaComponent);
    component = fixture.componentInstance;

    attivitaServiceSpy = TestBed.inject(AttivitaService) as jasmine.SpyObj<AttivitaService>;
    authServiceSpy = TestBed.inject(AuthService) as jasmine.SpyObj<AuthService>;
    routerSpy = TestBed.inject(Router) as jasmine.SpyObj<Router>;

    fixture.detectChanges();
  });

  it('should create', () => {
    expect(component).toBeTruthy();
  });

  it('should load activities on init', () => {
    const mockAttivita = [{ id: 1, titolo: 'Test', descrizione: 'Test desc', scadenza: '2023-01-01', stato: 'Da fare', idUser: '1' }];
    
    attivitaServiceSpy.getAttivitaByUser.and.returnValue(of(mockAttivita));
    
    component.ngOnInit();
    
    expect(component.attivitaList.length).toBe(1);
    expect(component.attivitaList[0].titolo).toEqual('Test');
  });

  it('should add activity', () => {
    const mockNewAttivita = { id: 2, titolo: 'New Test', descrizione: 'New desc', scadenza: '2023-02-01', stato: 'Da fare', idUser: '1' };
    
    authServiceSpy.getUserId.and.returnValue('1');
    attivitaServiceSpy.createAttivita.and.returnValue(of(mockNewAttivita));
    
    component.attivitaForm.setValue({
      id: 0, // Assicurati che l'ID iniziale sia 0 per una nuova attività
      titolo: 'New Test',
      descrizione: 'New desc',
      scadenza: '2023-02-01',
      stato: 'Da fare',
      idUser: '1'
    });
    component.addAttivita();
    
    expect(component.attivitaList.length).toBe(1);
    expect(component.attivitaList[0].titolo).toEqual('New Test');
  });

  it('should update activity', () => {
    const mockUpdatedAttivita = { id: 1, titolo: 'Updated Test', descrizione: 'Updated desc', scadenza: '2023-01-02', stato: 'In corso', idUser: '1' };

    component.attivitaList = [mockUpdatedAttivita];
    component.selectedAttivita = mockUpdatedAttivita;
    component.attivitaForm.setValue(mockUpdatedAttivita);
    
    attivitaServiceSpy.updateAttivita.and.returnValue(of(undefined));
    
    component.updateAttivita();
    
    expect(component.attivitaList[0].titolo).toEqual('Updated Test');
  });

  it('should delete activity', () => {
    const mockAttivita = { id: 1, titolo: 'Test to delete', descrizione: 'Delete desc', scadenza: '2023-01-01', stato: 'Da fare', idUser: '1' };
    
    component.attivitaList = [mockAttivita];
    
    attivitaServiceSpy.deleteAttivita.and.returnValue(of(undefined));
    
    component.deleteAttivita(mockAttivita.id);
    
    expect(component.attivitaList.length).toBe(0);
  });

  it('should reset form', () => {
    component.attivitaForm.setValue({
      id: 1,
      titolo: 'Test',
      descrizione: 'Test desc',
      scadenza: '2023-01-01',
      stato: 'Da fare',
      idUser: '1'
    });
    
    component.resetForm();
    
    expect(component.attivitaForm.value.titolo).toBe('');
  });

  it('should navigate to auth on logout', () => {
    component.logout();
    
    expect(routerSpy.navigate).toHaveBeenCalledWith(['/auth']);
  });
});
